import React, { Component } from 'react';
import MaterialTable from 'material-table';
import * as MoviesApi from "../crud/MoviesCrud";
class MovieList extends Component{
    state = { users: null}
    constructor(props) {
        super(props);
       
    }


    componentDidMount() {
        MoviesApi.getMoviesList().then((data) => {
     
         this.setState({ ...this.state, users: data.data})
         console.log(this.state);
        }).catch((error) => {
            console.log("Error fetching users", error);
        });
    }
    render() {
         const  {users}  = this.state;
         setTimeout(() => {
          console.log('This will run after 1 second!')
        }, 1000);
         console.log(users);
        return (
          
              <Portlet>
                    <PortletHeader
                        title="CSP User Accounts"
                        toolbar={(
                            <PortletHeaderToolbar>
                                <Link to="/addUser">
                                    <Button variant="contained" color="primary">
                                        <i className="fa fa-plus-circle" aria-hidden="true"></i>&nbsp;&nbsp;Create New user
                                    </Button>
                                </Link>
                            </PortletHeaderToolbar>
                        )}
                    />
                    <PortletBody>
          <MaterialTable
                            title=""
                            columns={[
                                {
                                    title: "Language",
                                    field: "Language",
                                }                                
                           
                            ]}
                            data={users}
                            options={{
                                sorting: true,
                                actionsColumnIndex: -1,
                                showTitle: false,
                                search: true,
                                searchFieldAlignment: "left"
                            }}
                           
                        />
                            </PortletBody>
                </Portlet>
            )
}
}
export default MovieList;
