import React, { useEffect, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import AvatarGroup from '@material-ui/lab/AvatarGroup';
import Tooltip from '@material-ui/core/Tooltip';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';
import * as MoviesApi from "../crud/MoviesCrud";

export default function MovieDetails(props) {
    const imdbID = props.location.state.imdbID;
    console.log(imdbID);

    const [state, setState] = useState({
       MovieDetails:null
    });
    useEffect(() => {
        console.log(imdbID)
        MoviesApi.getMoviesDetail(imdbID).then((data) => {
     
          
            setState({ ...state, MovieDetails:data.data });
           }).catch((error) => {
               console.log("Error fetching users", error);
           });

    }, []);


  return (
  


    <GridList cellHeight={160} className={classes.gridList} cols={3}>
    {state.MovieDetails.map((tile) => (
      <GridListTile key={tile.Stills}>
        <img src={tile.Stills} alt={tile.Stills} />
      </GridListTile>
    ))}
  </GridList>
  );
}
